jQuery(function() {
    var b = new Date,TimezoneOffset = -b.getTimezoneOffset() / 60, 
		endtime1 = "2017/3/15 00:00:00",
		endtime2 = "2016/12/20 21:53:50";
	
	var b1 = new Date(endtime1);
	var b2 = new Date(endtime2);
	
	if(b>b1){endtime1 = b.getFullYear()+1 + "/" + (b1.getMonth() + 1) + "/" + (b1.getDate()) + " " + b1.getHours() + ":" + b1.getMinutes() + ":" + b1.getSeconds()}
	b1 = new Date(endtime1);
	var result = b1-b,pixindex=1;
	
	if(result<(24*3600000))
		{pixindex = 2;}
	else if(result<(24*3600000*7))
		{pixindex = 1;}
	else
		{pixindex = 0;}
	
	if(b>b2){endtime2 = b.getFullYear()+1 + "/" + (b2.getMonth() + 1) + "/" + (b2.getDate()) + " " + b2.getHours() + ":" + b2.getMinutes() + ":" + b2.getSeconds()}
	
	var j = [{
			timeText: endtime1, //目标时间
			timeZone: TimezoneOffset, //时区
			style: "flip",
			color: "black",
			width: 0,
			textGroupSpace: 15,
			textSpace: 0,
			reflection: !0,
			reflectionOpacity: 10,
			reflectionBlur: 0,
			dayTextNumber: 3,
			displayDay: !0,
			displayHour: !0,
			displayMinute: !0,
			displaySecond: !0,
			displayLabel: !0,
			onFinish: function() {window.location.reload()}
		},
		{
			timeText: endtime2, //目标时间
			timeZone: TimezoneOffset, //时区
			style: "flip",
			color: "black",
			width: 0,
			textGroupSpace: 15,
			textSpace: 0,
			reflection: !0,
			reflectionOpacity: 10,
			reflectionBlur: 0,
			dayTextNumber: 3,
			displayDay: !0,
			displayHour: !0,
			displayMinute: !0,
			displaySecond: !0,
			displayLabel: !0,
			onFinish: function() {window.location.reload()}
		}];
		var i = jQuery("#content"), e = jQuery("#content>.page");
		var mainbody = j[0],
        k = e;
        k.children(".countdown").jCountdown(mainbody);
		k.children(".countdown").addClass("background2");
        i.append(k);
		pics = k.children(".backgrounds").find(".slider-panel");
		pics.hide();
		pics.eq(pixindex).show();

});